div.script:
document.getElementById("sendOtpButton").addEventListener("click", function () {
    let email = document.getElementById("emailInput").value.trim();

    if (!email) {
        alert("Please enter an email!");
        return;
    }

    let formData = new FormData();
    formData.append("email", email);
    formData.append("csrf_token", document.querySelector("[name='csrf_token']").value);

    fetch("/request_subdomain/create/otp", {
        method: "POST",
        body: formData
    })
        .then(response => response.json())
        .then(data => {
            if (data.status === "success") {
                alert("OTP Sent Successfully!");

                // Hide email section and show OTP section
                document.getElementById("emailSection").style.display = "none";
                document.getElementById("otpSection").style.display = "block";

                // Store email in hidden input for OTP verification
                document.getElementById("hiddenEmail").value = email;
            } else {
                alert(data.message);
            }
        })
        .catch(error => console.error("Error:", error));
});

document.getElementById("verifyOtpButton").addEventListener("click", function () {
    let email = document.getElementById("hiddenEmail").value;
    let otp = document.getElementById("otpInput").value.trim();

    if (!otp) {
        alert("Please enter the OTP!");
        return;
    }

    let formData = new FormData();
    formData.append("email", email);
    formData.append("otp", otp);
    formData.append("csrf_token", document.querySelector("[name='csrf_token']").value);

    fetch("/request_subdomain/verify_otp", {
        method: "POST",
        body: formData
    })
        .then(response => response.json())
        .then(data => {
            if (data.status === "success") {
                // alert("OTP Verified Successfully! Proceeding...");
                window.location.href = "/my/request_subdomain";
            } else {
                alert(data.message);
            }
        })
        .catch(error => console.error("Error:", error));
});
